ution() {\n        document.getElementById('solution').innerText = \"Soultion Goes Here\";\n      }\n    </script>\n  </div>\n  <p id=\"solution\"></p>\n  <div id=\"color_select\" class =\"color-selector\">\n    <button onclick=\"myFunction('red')\" class=\"selector red-selector\"> </button>\n    <button onclick=\"myFunction('blue')\" class=\"selector blue-selector\"></button>\n    <button onclick=\"myFunction('yellow')\" class=\"selector yellow-selector\"></button>\n    <button onclick=\"myFunction('green')\" class=\"selector green-selector\"></button>\n    <button onclick=\"myFunction('white')\" class=\"selector white-selector\"></button>\n    <button onclick=\"myFunction('orange')\" class=\"selector orange-selector\"></button>\n    <script>\n      function myFunction(clr) {\n        document.getElementById(\"color_select\").style.backgroundColor = clr;\n      }\n    </script>\n  </div>\n  <div class= \"outer-grid\">\n    <div class=\"grid-container\">\n    </div>\n    <div class=\"grid-container\">\n      <div id=\"U1\" onclick=\"colorChange('U1')\" class=\"grid-item\"></div>\n      <div id=\"U2\" onclick=\"colorChange('U2')\" class=\"grid-item\"></div>\n      <div id=\"U3\" onclick=\"colorChange('U3')\" class=\"grid-item\"></div>\n      <div id=\"U4\" onclick=\"colorChange('U4')\" class=\"grid-item\"></div>\n      <div class=\"U5\"></div>\n      <div id=\"U6\" onclick=\"colorChange('U6')\" class=\"grid-item\"></div>\n      <div id=\"U7\" onclick=\"colorChange('U7')\" class=\"grid-item\"></div>\n      <div id=\"U8\" onclick=\"colorChange('U8')\" class=\"grid-item\"></div>\n      <div id=\"U9\" onclick=\"colorChange('U9')\"class=\"grid-item\"></div>\n    </div> \n    <div class=\"grid-container\">\n    </div>\n    <div class=\"grid-container\">\n    </div>\n    <div class=\"grid-container\">\n      <div id=\"L1\" onclick=\"colorChange('L1')\" class=\"grid-item\"></div>\n      <div id=\"L2\" onclick=\"colorChange('L2')\" class=\"grid-item\"></div>\n      <div id=\"